import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import styled from "styled-components";

const Detail = () => {
  let params = useParams();
  const [details, setDetails] = useState({});

  useEffect(() => {
    const getDetails = async () => {
      const api = await fetch(
        `https://api.themoviedb.org/3/movie/${params.id}?api_key=63eed5025c582e163ec280bf8701d6b5&language=en-US`
      );
      const data = await api.json();
      setDetails(data);
      console.log(data);
    };
    getDetails();
  }, [params.id]);
  return (
    <Container>
      <img className="background-poster"
        src={`https://image.tmdb.org/t/p/original${details.backdrop_path}`}
        alt={params.title}
      />
      <Content>
        <div className="movie-detail">
          <h1>{details.title}</h1>
          <p>Genres:
            {details.genres?.map((item, index) => {
              return <span key={item.id}> {(index ? ' | ' : '') + item.name}</span>;
            })}
          </p>
          <p className="movie-overview">{details.overview}</p>
        </div>
        <div className="movie-img">
            <img src={`https://image.tmdb.org/t/p/original${details.poster_path}`} alt={details.title}/>
        </div>
      </Content>
    </Container>
  );
};

const Container = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
  .background-poster {
    width: 100%;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    filter: brightness(60%);
  }
`;

const Content = styled.div`
  display: flex;
  position: absolute;
  top: 0;
  transform: translate(5%,50%);
  .movie-detail {
    max-width: 50%;
    transform: translateY(10%);
  }
  .movie-img img{
   width: 50%;
  }
`;

export default Detail;
