import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import styled from "styled-components";

const Detail = () => {
  let params = useParams();
  const [details, setDetails] = useState({});

  useEffect(() => {
    const getDetails = async () => {
      const api = await fetch(
        `https://api.themoviedb.org/3/movie/${params.id}?api_key=63eed5025c582e163ec280bf8701d6b5&language=en-US`
      );
      const data = await api.json();
      setDetails(data);
      console.log(data);
    };
    getDetails();
  }, [params.id]);

  return (
    <Container>
      <img className="background" src={`https://image.tmdb.org/t/p/original${details.backdrop_path}`} alt={params.title} />
    </Container>
  );
};

const Container = styled.div`
    position: relative;
    width: 100%;
    height: 100%;
`;

export default Detail;
