import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

import { Swiper, SwiperSlide } from "swiper/react";
import { Autoplay } from "swiper";
import "swiper/css";
import "swiper/css/autoplay";
import "./swiper.css";

import styled from "styled-components";


const MainSlider = () => {
  const [movies, setMovies] = useState([]);
  

  useEffect(() => {
    const getMovies = async () => {
      const api = await fetch(
        `https://api.themoviedb.org/3/movie/popular?api_key=${process.env.REACT_APP_API_KEY}&language=en-US&page=1`
      );

      const data = await api.json();
      setMovies(data.results.slice(0, 4));
    };
    getMovies();
  }, []);

  return (
    <>
      <Swiper
        // modules={[Autoplay]}
        loop={true}
        autoplay={{ delay: 5000 }}
        slidesPerView={1}
        scrollbar={{ draggable: true }}
      >
        {movies.map((item) => {
          return (
              <SwiperSlide key={item.id}>
                <MainSlideItem item={item}/>
              </SwiperSlide>
          );
        })}
      </Swiper>
      {movies.map((item) => {
        return <ModalTrailer key={item.id} item={item}/>
      })}
    </>
  );
};


const MainSlideItem = ({ item }) => {
  const navigate = useNavigate();
  const backdropPath = `https://image.tmdb.org/t/p/original${item.backdrop_path}`;
  const posterPath = `https://image.tmdb.org/t/p/original${item.poster_path}`;
  const [trailers, setTrailers] = useState([]);
  useEffect(() => {
    const getTrailers = async () => {
      const api = await fetch(
        `https://api.themoviedb.org/3/movie/${item.id}/videos?api_key=${process.env.REACT_APP_API_KEY}&language=en-US`
      );
      const data = await api.json();
      setTrailers(data.results[0]);
    };
    getTrailers();
  }, [item.id]);

  const handleClick = (id) => {
    navigate("/detail/" + id);
  };

  const setModal = async () => {
    const videoTrailer = `https://www.youtube.com/embed/${trailers.key}`;
    const modal = document.querySelector(`#modal_${item.id}`);
    modal.querySelector(".trailer-iframe").setAttribute("src", videoTrailer);
  };

  return (
    <div className="main-slide">
      <img className="slide-background" src={backdropPath} alt={item.title} />
      <div className="movie-info">
        <h1 className="movie-title">{item.title}</h1>
        <p className="movie-description">{item.overview}</p>
        <div className="movie-action">
          <button
            className="movie-watch-now"
            onClick={() => handleClick(item.id)}
          >
            Watch now
          </button>
          <button className="movie-trailer" onClick={setModal}>
            trailer
          </button>
        </div>
      </div>
      <div className="movie-poster">
        <img src={posterPath} alt={item.title} />
      </div>
    </div>
  );
};

const ModalTrailer = ({ item }) => {
  
  return (
    <Video id={`modal_${item.id}`}>
      <p className="trailer-title">{item.name}</p>
      <iframe className="trailer-iframe"
        title="trailer"
      ></iframe>
    </Video>
  );
};

const Video = styled.div`
  margin: 2rem 5rem;
  .trailer-title {
    font-size: 1.5rem;
    font-weight: 500;
    color: #12c6b2;
  }
  iframe {
    width: 100%;
    height: 700px;
  }
`;







export default MainSlider;
