import React, { useState, useEffect } from "react";
//import { Navigation, Pagination, Scrollbar, A11y } from "swiper";

import { Swiper, SwiperSlide } from "swiper/react";

import "swiper/css";

import "./swiper.css";

const MainSlider = () => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    const getMovies = async () => {
      const api = await fetch(
        `https://api.themoviedb.org/3/movie/popular?api_key=${process.env.REACT_APP_API_KEY}&language=en-US&page=1`
      );

      const data = await api.json();
      setMovies(data.results.slice(0, 4));
      console.log(movies);
    };
    getMovies();
  }, []);

  return (
    <Swiper
      spaceBetween={50}
      slidesPerView={1}
      pagination={{ clickable: true }}
      scrollbar={{ draggable: true }}
    >
      {movies.map((movie) => {
        const { id, title, overview, backdrop_path } = movie;
        const imgPath = `https://image.tmdb.org/t/p/original${backdrop_path}`;
        return (
          <SwiperSlide key={id}>
            <div className="main-slide">
              <img className="slide-background" src={imgPath} alt={title} />
              <div className="movie-info">
                <h1 className="movie-title">{title}</h1>
                <p className="movie-description">{overview}</p>
                <div className="movie-action">
                  <button className="movie-watch-now">Watch now</button>
                  <button className="movie-trailer">Watch trailer</button>
                </div>
              </div>
            </div>
          </SwiperSlide>
        );
      })}
    </Swiper>
  );
};

export default MainSlider;
