import styled from "styled-components";
import {useRef} from "react"
const ModalTrailer = ({ item }) => {
    const iframeRef = useRef(null);

    const onClose = () => iframeRef.current.setAttribute('src', '');
    return (
      <Video id={`modal_${item.id}`}>
          <button onClick={onClose}>CLick</button>
        <p className="trailer-title">{item.name}</p>
        <iframe ref={iframeRef} className="trailer-iframe"
          title="trailer"
        ></iframe>
      </Video>
    );
  };
  
  const Video = styled.div`
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    margin: 2rem 5rem;
    visibility: hidden;
    z-index: 99999;
    &.active {
        visibility: visible;
    }
    .trailer-title {
      font-size: 1.5rem;
      font-weight: 500;
      color: #12c6b2;
    }
    iframe {
        width: 100%;
        height: 700px;
    }
  `;

  export default ModalTrailer
