import styled from "styled-components";

const ModalTrailer = ({ item }) => {
  
    return (
      <Video id={`modal_${item.id}`}>
        <p className="trailer-title">{item.name}</p>
        <iframe className="trailer-iframe"
          title="trailer"
        ></iframe>
      </Video>
    );
  };
  
  const Video = styled.div`
    position: absolute;
    top: 0;
    left: 0;
    margin: 2rem 5rem;
    visibility: visible;
    z-index: 99999;
    &.active {
        visibility: visible;
    }
    .trailer-title {
      font-size: 1.5rem;
      font-weight: 500;
      color: #12c6b2;
    }
    iframe {
      width: 1000px;
      height: 700px;
      
    }
  `;

  export default ModalTrailer
