import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import styled from "styled-components";
import CasterList from "../components/Caster/CasterList";

const Detail = () => {
  let params = useParams();
  console.log(params);
  const [details, setDetails] = useState({});
  const [readMore, setReadMore] = useState(false);

  useEffect(() => {
    const getDetails = async () => {
      const api = await fetch(
        `https://api.themoviedb.org/3/movie/${params.id}?api_key=63eed5025c582e163ec280bf8701d6b5&language=en-US`
      );
      const data = await api.json();
      setDetails(data);
    };
    getDetails();
  }, [params.id]);
  return (
    <Container>
      <img
        className="background-poster"
        src={`https://image.tmdb.org/t/p/original${details.backdrop_path}`}
        alt={params.title}
      />
      <Content>
        <div className="movie-detail-img">
          <img
            src={`https://image.tmdb.org/t/p/original${details.poster_path}`}
            alt={details.title}
          />
        </div>
        <div className="movie-detail">
          <div className="movie-detail-title">
            <h1>{details.title}</h1>
          </div>
          <p className="genres">
            {details.genres?.map((item) => {
              return (
                <span key={item.id} className="genres-border">
                  {" "}
                  {item.name}
                </span>
              );
            })}
          </p>
          <p className="movie-detail-overview">
            {readMore ? details.overview : `${details.overview?.substring(0, 200)}...`}
            <button onClick={() => setReadMore(!readMore)}>{readMore ? 'show less' : 'read more'}</button>
          </p>
          <div className="movie-detail-caster">
            <h3>Casters</h3>
            <CasterList id={params.id} />
          </div>
        </div>
      </Content>
    </Container>
  );
};

const Container = styled.div`
  position: relative;
  .background-poster {
    width: 100%;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    filter: brightness(30%);
  }
`;

const Content = styled.div`
  display: flex;
  position: absolute;
  margin: 10rem 15rem;
  top: 0;
  .movie-detail {
    .movie-detail-title {
      width: 100%;
      min-height: 7rem;
      h1 {
        font-size: 2.5rem;
      }
    }
    .genres {
      font-size: 0.8rem;
      font-weight: 400;
      letter-spacing: 0.1rem;
      .genres-border {
        display: inline-block;
        padding: 0.2rem 0.4rem;
        border: 2px solid white;
        border-radius: 1rem;
        margin-right: 0.5rem;
        margin-bottom: 0.5rem;
      }
    }
    .movie-detail-overview {
      font-size: 1rem;
      font-weight: 400;
      letter-spacing: 0.05rem;
      margin-bottom: 1rem;
      background-color: red;
    }
    .movie-detail-caster {
      position: absolute;
      bottom: 0;
    }
  }
  .movie-detail-img {
    min-width: 20rem;
    width: 100%;
    margin-right: 2rem;
    img {
      width: 100%;
      height: 100%;
      background-position: center;
      background-repeat: no-repeat;
      background-size: cover;
      border-radius: 0.8rem;
    }
  }
`;

export default Detail;
