import { Link } from "react-router-dom";
import React, { useState, useEffect } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";

import Card from "../card-movie/Card"

const SimilarSlider = (props) => {
    const [movies, setMovies] = useState([]);
  
    useEffect(() => {
      const getMovies = async () => {
        const api = await fetch(
          `https://api.themoviedb.org/3/movie/${props.id}/similar?api_key=${process.env.REACT_APP_API_KEY}&language=en-US&page=1`
        );
        const data = await api.json();
        setMovies(data);
      };
      getMovies();
    }, [props.id]);

  return (
    <Swiper
        spaceBetween={10}
        slidesPerView={5}
        scrollbar={{ draggable: true }}
      >
        {movies.map((item) => {
          const posterPath = `https://image.tmdb.org/t/p/original${item.poster_path}`;
          return (
            <SwiperSlide key={item.id}>
              <Card item={item}  posterPath={posterPath}/>
            </SwiperSlide>
          );
        })}
      </Swiper>
  )
}

export default SimilarSlider
