import React, { useState, useEffect } from "react";
import styled from "styled-components";

const Movies = () => {
  const [movies, setMovies] = useState([]);
  const [showMore, setShowMore] = useState(1);
  console.log(showMore);
  useEffect(() => {
    const getMovies = async () => {
      const api = await fetch(
        `https://api.themoviedb.org/3/movie/popular?api_key=${process.env.REACT_APP_API_KEY}&language=en-US&page=${showMore}`
      );

      const data = await api.json();
      setMovies(data.results);
    };
    getMovies();
  }, []);

  const handleShowMore = async () => {
    setShowMore(showMore + 1);
    const api = await fetch(
      `https://api.themoviedb.org/3/movie/popular?api_key=${process.env.REACT_APP_API_KEY}&language=en-US&page=${showMore}`
    );
    const data = await api.json();
    setMovies([...movies, ...data.results]);
  };

  useEffect(() => {
    handleShowMore();
  });

  return (
    <>
      <Grid>
        {movies.map((item) => {
          const posterPath = `https://image.tmdb.org/t/p/original${item.poster_path}`;
          return (
            <Card key={item.id}>
              <img src={posterPath} alt={item.title}></img>
              <h4>{item.title}</h4>
            </Card>
          );
        })}
      </Grid>
      <Button>
        <button onClick={() => handleShowMore()}>Load more</button>
      </Button>
    </>
  );
};

const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(15rem, 1fr));
  grid-gap: 1rem;
  padding: 10rem 5rem 0.5rem 5rem;
`;

const Card = styled.div`
  img {
    width: 100%;
    border-radius: 0.8rem;
  }

  a {
    text-decoration: none;
  }

  h4 {
    text-align: center;
    padding: 1rem;
  }
`;

const Button = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  button {
    background-color: transparent;
    color: #12c6b2;
    font-size: 1rem;
    border: 2px solid #12c6b2;
    border-radius: 0.3rem;
    padding: 0.3rem 0.5rem;
    cursor: pointer;
    &:hover {
      color: white;
      background-color: #12c6b2;
    }
  }
`;

export default Movies;
