import React, { useState, useEffect } from "react";

const Trailer = (props) => {
  const [trailers, setTrailers] = useState([]);
  console.log(trailers);
  useEffect(() => {
    const getTrailers = async () => {
      const api = await fetch(
        `https://api.themoviedb.org/3/movie/${props.id}/videos?api_key=${process.env.REACT_APP_API_KEY}&language=en-US`
      );
      const data = await api.json();
      setTrailers(data.results.slice(0, 3));
    };
    getTrailers();
  }, [props.id]);
  return (
    <div>{trailers.map((item) => {
        return (
            <iframe key={item.key} src={`https://www.youtube.com/embed/${item.key}`} width="100%" height="500px" title="trailer"></iframe>
        )
    })}
    </div>
  )
};

export default Trailer;
