import React, { useState, useEffect } from "react";
import styled from "styled-components";
const ModalTrailer = ({ item }) => {
  
  return (
    <Video id={`modal_${item.id}`}>
      <p className="trailer-title">{item.name}</p>
      <iframe className="trailer-iframe"
        src={`https://www.youtube.com/embed/${item.key}`}
        title="trailer"
      ></iframe>
    </Video>
  );
};

const Video = styled.div`
  margin: 2rem 5rem;
  .trailer-title {
    font-size: 1.5rem;
    font-weight: 500;
    color: #12c6b2;
  }
  iframe {
    width: 100%;
    height: 700px;
  }
`;

export default ModalTrailer;
