import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import styled from "styled-components";

const Detail = () => {
  let params = useParams();
  const [details, setDetails] = useState({});

  useEffect(() => {
    const getDetails = async () => {
      const api = await fetch(
        `https://api.themoviedb.org/3/movie/${params.id}?api_key=63eed5025c582e163ec280bf8701d6b5&language=en-US`
      );
      const data = await api.json();
      setDetails(data);
      console.log(data);
    };
    getDetails();
  }, [params.id]);
  return (
      <>
      <img
        className="background-poster"
        src={`https://image.tmdb.org/t/p/original${details.backdrop_path}`}
        alt={params.title}
      />
      <Container>
      <div className="movie-detail">
        <h1>{details.title}</h1>
        <p>
          Genres:
          {details.genres?.map((item, index) => {
            return (
              <span key={item.id}> {(index ? " | " : "") + item.name}</span>
            );
          })}
        </p>
        <p className="movie-overview">{details.overview}</p>
      </div>
      <div className="movie-img">
        <img
          src={`https://image.tmdb.org/t/p/original${details.poster_path}`}
          alt={details.title}
        />
      </div>
    </Container>
      </>
    
  );
};

const Container = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
  .background-poster {
    width: 100%;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    filter: brightness(60%);
  }
  .movie-detail {
    position: absolute;
    max-width: 50%;
    height: 100%;
    top: 0;
    left: 0;
    transform: translate(10%, 30%);
    background: red;
  }
  .movie-img {
      position: absolute;
      top: 0;
      img {
          width: 50%;
          transform: translate(50%, 50%);
      }
  }
`;


export default Detail;
