import React, { useState, useEffect } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import styled from "styled-components";
import Card from "../card-movie/Card";

const Slider = (props) => {
  const [movies, setMovies] = useState([]);
    
  useEffect(() => {
    const getMovies = async () => {
        let url = '';
        switch (props.type) {
            case 'similar': 
                url = `https://api.themoviedb.org/3/movie/${props.id}/similar?api_key=${process.env.REACT_APP_API_KEY}&language=en-US&page=1`
                break;
            default: url= 'none';
        }
      const api = await fetch(url);
      const data = await api.json();
      setMovies(data.results);
    };
    getMovies();
}, [props.id, props.type]);

console.log(movies);
  console.log(props);
  return (
    <SSwiper spaceBetween={10} slidesPerView={5} scrollbar={{ draggable: true }}>
        {movies.map((item) => {
            const posterPath = `https://image.tmdb.org/t/p/original${item.poster_path}`;
            return (
                <SwiperSlide key={item.id}>
                    <Card item={item} posterPath={posterPath}/>
                </SwiperSlide>
            )
        })}
    </SSwiper>
  );
};

const SSwiper = styled(Swiper)`
    width: 100%;
    
`

export default Slider;
