import React from "react";
import { NavLink, Link } from "react-router-dom";
import styled from "styled-components";
import logo from "../../images/Hmovie.png";
import "./Header.css"
const Header = () => {
  return (
    <>
      <Nav>
        <Link to="/">
          <img src={logo} alt="logo" />
        </Link>
        <NavMenu>
          <SLink to="/">
            <h4>Home</h4>
          </SLink>
          <SLink to="/movies">
            <h4>Movies</h4>
          </SLink>
          <SLink to="/upcoming">
            <h4>Upcoming</h4>
          </SLink>
        </NavMenu>
      </Nav>
    </>
  );
};

const Nav = styled.nav`
    display: flex;
    background: grey;
`;

const NavMenu = styled.div`
    width: 100%;
    display: flex;
    justify-content: center;
`;
const SLink = styled(NavLink)`
    position: relative;
    padding: 1rem;
    font-size: 1.1rem;
    color: white;
    text-decoration: none;
    padding: 1.2rem 1.5rem;
    h4 {
      font-weight: 500;
      transition: 1s;
    }
    &::before {
      position: absolute;
      content: "";
      width: 100%;
      height: 3px;
      bottom: 0;
      left: 0;
      background-color: #12c6b2;
      visibility: hidden;
      
    }
    &.active::before {
      visibility: visible;
    }
    &.active {
      color: #12c6b2;
    }
`;


export default Header;
