import React, { useState, useEffect } from "react";
import styled from "styled-components";

const Trailer = (props) => {
  const [trailers, setTrailers] = useState([]);
  console.log(trailers);
  useEffect(() => {
    const getTrailers = async () => {
      const api = await fetch(
        `https://api.themoviedb.org/3/movie/${props.id}/videos?api_key=${process.env.REACT_APP_API_KEY}&language=en-US`
      );
      const data = await api.json();
      setTrailers(data.results.slice(0, 3));
    };
    getTrailers();
  }, [props.id]);
  return (
    <div>{trailers.map((item) => {
        return (
            <Video>
                <h3>{item.name}</h3>
                <iframe key={item.key} src={`https://www.youtube.com/embed/${item.key}`} title="trailer"></iframe>
            </Video>
        )
    })}
    </div>
  )
};

const Video = styled.div`
    margin: 0 5rem;
    iframe {
        width: 100%;
        height: 700px;
    }
`

export default Trailer;
