import React, { useState, useEffect } from "react";

import { Swiper, SwiperSlide } from "swiper/react";
import { Autoplay } from "swiper";
import "swiper/css";
import "./swiper.css";
import "swiper/css/autoplay";
import MainSlideItem from "./MainSlideItem";
import ModalTrailer from "./ModalTrailer";

const MainSlider = () => {
  const [movies, setMovies] = useState([]);
  const [modal, setModal] = useState(false);
  const [trailers, setTrailers] = useState([]);
  
  console.log(modal);
  useEffect(() => {
    const getMovies = async () => {
      const api = await fetch(
        `https://api.themoviedb.org/3/movie/popular?api_key=${process.env.REACT_APP_API_KEY}&language=en-US&page=1`
      );

      const data = await api.json();
      setMovies(data.results.slice(0, 4));
    };
    const getTrailers = async () => {
      const api = await fetch(
        `https://api.themoviedb.org/3/movie/${movies.id}/videos?api_key=${process.env.REACT_APP_API_KEY}&language=en-US`
      );
      const data = await api.json();
      setTrailers(data.results.slice(0, 4));
    };
    getTrailers();
    getMovies();
  }, []);

  

  // useEffect(() => {
  //   const getTrailers = async () => {
  //     const api = await fetch(
  //       `https://api.themoviedb.org/3/movie/${movies.id}/videos?api_key=${process.env.REACT_APP_API_KEY}&language=en-US`
  //     );
  //     const data = await api.json();
  //     setTrailers(data.results.slice(0, 4));
  //   };
  //   getTrailers();
  // }, [movies.id]);

  return (
    <>
      <Swiper
        // modules={[Autoplay]}
        loop={true}
        autoplay={{ delay: 5000 }}
        slidesPerView={1}
        scrollbar={{ draggable: true }}
      >
        {movies.map((item) => {
          return (
              <SwiperSlide key={item.id}>
                <MainSlideItem item={item} setModal={setModal}/>
              </SwiperSlide>
          );
        })}
      </Swiper>
      {trailers.map((item) => {
        return <ModalTrailer key={item.id} item={item} modal={modal}/>
      })}
    </>
  );
};

export default MainSlider;
