import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Swiper, SwiperSlide } from "swiper/react";
import { Autoplay } from "swiper";
import "swiper/css";
import "./swiper.css";
import "swiper/css/autoplay";
import styled from "styled-components";

const MainSlider = () => {
  const [movies, setMovies] = useState([]);
  const [value, setValue] = useState(0);
  const navigate = useNavigate();
  console.log(value);

  useEffect(() => {
    const getMovies = async () => {
      const api = await fetch(
        `https://api.themoviedb.org/3/movie/popular?api_key=${process.env.REACT_APP_API_KEY}&language=en-US&page=1`
      );

      const data = await api.json();
      setMovies(data.results.slice(0, 4));
    };
    getMovies();
  }, []);

  const handleClick = (id) => {
    navigate("/detail/" + id);
  };

  return (
    <>
      <Swiper
        modules={[Autoplay]}
        loop={true}
        autoplay={{ delay: 5000 }}
        slidesPerView={1}
        scrollbar={{ draggable: true }}
      >
        {movies.map((item) => {
          const backdropPath = `https://image.tmdb.org/t/p/original${item.backdrop_path}`;
          const posterPath = `https://image.tmdb.org/t/p/original${item.poster_path}`;
          return (
            <div key={item.id}>
              <SwiperSlide>
                <div className="main-slide">
                  <img
                    className="slide-background"
                    src={backdropPath}
                    alt={item.title}
                  />
                  <div className="movie-info">
                    <h1 className="movie-title">{item.title}</h1>
                    <p className="movie-description">{item.overview}</p>
                    <div className="movie-action">
                      <button
                        className="movie-watch-now"
                        onClick={() => handleClick(item.id)}
                      >
                        Watch now
                      </button>
                      <button
                        className="movie-trailer"
                        onClick={() => setValue(item.id)}
                      >
                        trailer
                      </button>
                    </div>
                  </div>
                  <div className="movie-poster">
                    <img src={posterPath} alt={item.title} />
                  </div>
                </div>
              </SwiperSlide>
              <ModalTrailer className={`"modal-test" ${item.id === value ? "active" : ""}`}>
                Hello {item.id}
              </ModalTrailer>
            </div>
          );
        })}
      </Swiper>
    </>
  );
};

const ModalTrailer = styled.div`
  visibility: hidden;
  &.active {
    visibility: visible;
  }
`;

export default MainSlider;
